CREATE TABLE products(
    product_id SERIAL PRIMARY KEY NOT NULL, title VARCHAR(50) NOT NULL, description VARCHAR(500), funds_goal INT NOT NULL, funds_raised INT, images TEXT[], creator_name VARCHAR(50), creator_email VARCHAR(100) NOT NULL, created_at TIMESTAMPTZ DEFAULT NOW()
);

INSERT INTO products(title, description, funds_goal, funds_raised, images, creator_name, creator_email) VALUES ('Laundry nugget', 'This washing machine will save on electricy.Manually fill it with water, toss in the laundry detergent of your choice, and pedal away. Not only good for the environment but great for a daily workout. Typical wash time ranges depending on speed of pedaling: generally around 10 minutes. Drain and refill with rinse water and pedal for 5-10 minutes longer. Drain and dry.', 50000, 500, '{"https://www.simplemost.com/wp-content/uploads/2018/05/drumi-machine.jpg", "https://www.ecohome.net/media/articles/images/76/a2/76a2236fb79ce89955710b6313bb7feea0c78136/76a2236fb79ce89955710b631_UeJzp0a.jpg", "https://resize.indiatvnews.com/en/resize/newbucket/1200_-/2017/06/txog9jfao1gexhxtjag42d3hd-1496468393.jpg"}', 'Rooster Cogburn', 'roosterCog@hotmail.com');

INSERT INTO products(title, description, funds_goal, funds_raised, images, creator_name, creator_email) VALUES ('FUFOFA | Get a Perfect Tan Right Through Your Suit', 'Stunning, shaping, quick-dry swimwear that enables you to tan through your suit to give you an even bronze.', 35000, 12000, '{"https://ksr-ugc.imgix.net/assets/035/498/146/212b5dbc2fd3e567a811bedfe7903173_original.jpg?ixlib=rb-4.0.2&crop=faces&w=1024&h=576&fit=crop&v=1636280022&auto=format&frame=1&q=92&s=2f37a09bc8d74d911d59104b12997f03", "https://cdn.shopify.com/s/files/1/0742/1299/products/bali-tan-through-support-top-swimsuit-263503.jpg?v=1637315437", "https://cdn.shopify.com/s/files/1/1753/9899/files/sun-through_crop_large.jpg?v=1521475373"}', 'Rooster Cogburn', 'roosterCog@hotmail.com');

INSERT INTO products(title, description, funds_goal, funds_raised, images, creator_name, creator_email) VALUES ('Freezerwabe', 'Instead of heating things up instantaneously, this device freezes food items almost instantly.', 92000, 10, '{"https://i.dailymail.co.uk/i/pix/2015/02/09/video-undefined-2580A06700000578-921_636x358.jpg"}', 'Buddy McGurck', 'mcgurckuhgurk@gurk.com');
